mapper.select:
select t1.item_code mylabel, ifnull(재고금액,0) myvalue from (select 일자, t1.item_code, item_name, 재고금액, mc_code, mc_name, sc_code, sc_name from (select t1.일자, t1.item_code,입고량, 출고량,sum(ifnull(입고량,0)-ifnull(출고량,0)) over (partition by item_code order by 일자) as 재고량, (c.supply_price*sum(ifnull(입고량,0)-ifnull(출고량,0)) over (partition by item_code order by 일자)) 재고금액 from (select t1.일자 as 일자, t1.item_code as item_code, 입고량 from (select * from (WITH RECURSIVE CTE AS ( SELECT DATE_FORMAT('20230101', '%Y-%m-%d') AS DT FROM DUAL UNION ALL SELECT DATE_ADD(DT, INTERVAL 1 DAY) FROM CTE WHERE DT < DATE_FORMAT(#{startDate}, '%Y-%m-%d')) SELECT DT AS 일자 FROM CTE) t1, -- 기간내의 날짜 모두 출력 (select distinct(item_code) from inbound union select distinct(item_code) from existing_stock) t2) t1 -- 모든 날짜(t1)와 재고가 있는 아이템 코드(t2)의 조인 >> from절에 ,로 두개 테이블을 적으면 크로스 조인(모든 경우의 수 row를 가진 테이블 출력, 날짜별 모든 품목코드를 가짐) left join (select item_code,DATE_FORMAT(es_date, '%Y-%m-%d') 일자, es_quantity 입고량 from existing_stock union all select item_code, DATE_FORMAT(arrival_date, '%Y-%m-%d') 일자, sum(inbound_quantity) 입고량 from inbound group by item_code ,DATE_FORMAT(arrival_date, '%Y-%m-%d')) t2 on t1.일자=t2.일자 and t1.item_code=t2.item_code) t1 left join ( select item_code, DATE_FORMAT(shipping_date, '%Y-%m-%d') 일자, sum(outbound_quantity) 출고량 from outbound -- 기존재고량, 입고량, 출고량 조인 group by item_code ,DATE_FORMAT(shipping_date, '%Y-%m-%d')) t2 on t1.일자=t2.일자 and t1.item_code=t2.item_code left join (select item_code, item_name, max(supply_price) supply_price from contract join item using(item_code) group by item_code, item_name) c on t1.item_code=c.item_code) t1 left join (select * from item join sub_category using(sc_code) join major_category using(mc_code)) t2 on t1.item_code=t2.item_code) t1 join (select item_code, max(일자) 일자 from ( select t1.일자, t1.item_code,입고량, 출고량,sum(ifnull(입고량,0)-ifnull(출고량,0)) over (partition by item_code order by 일자) as 재고량 from (select t1.일자 as 일자, t1.item_code as item_code, 입고량 from (select * from (WITH RECURSIVE CTE AS ( SELECT DATE_FORMAT('20230101', '%Y-%m-%d') AS DT FROM DUAL UNION ALL SELECT DATE_ADD(DT, INTERVAL 1 DAY) FROM CTE WHERE DT < DATE_FORMAT(#{startDate}, '%Y-%m-%d')) SELECT DT AS 일자 FROM CTE) t1, (select distinct(item_code) from inbound union select distinct(item_code) from existing_stock) t2) t1 left join (select item_code,DATE_FORMAT(es_date, '%Y-%m-%d') 일자, es_quantity 입고량 from existing_stock union all select item_code, DATE_FORMAT(arrival_date, '%Y-%m-%d') 일자, sum(inbound_quantity) 입고량 from inbound group by item_code ,DATE_FORMAT(arrival_date, '%Y-%m-%d')) t2 on t1.일자=t2.일자 and t1.item_code=t2.item_code) t1 left join ( select item_code, DATE_FORMAT(shipping_date, '%Y-%m-%d') 일자, sum(outbound_quantity) 출고량 from outbound group by item_code ,DATE_FORMAT(shipping_date, '%Y-%m-%d')) t2 on t1.일자=t2.일자 and t1.item_code=t2.item_code) t1 group by item_code) t2 on t1.item_code=t2.item_code and t1.일자 = t2.일자
where 재고금액 >= #{num}
order by t1.item_code
